import React from "react";
import axios from "axios";
import "bulma/css/bulma.css";
import { Link } from "react-router-dom";

function MyHabitButton(props) {
  function addToMyHabits() {
    const { id } = props.match.params.id; //id van habit
    const { userId } = props.loggedInUser._id; //id van user

    axios
      .post(`http://localhost:5000/api/explore/${id}/favorites`,  userId ) //userId stuur je nu naar de server
      .then((response) => {
        console.log(props.loggedInUser._id);
      })
      .catch((error) => console.error(error));
  }
  return (
    <div>
      <form>
        <Link to="/explore/:id">
          <button onClick={addToMyHabits}>Add to myHabits</button>
        </Link>
      </form>
    </div>
  );
}

export default MyHabitButton;
