import React from "react";
import axios from "axios";
import "bulma/css/bulma.css";
import { Link } from "react-router-dom";

function MyHabitButton(props) {
  function addToMyHabits() {
    const { id } = props.match.params //id van habit
    const { userId} = props.loggedInUser._id; //id van user

    axios
      .post(`http://localhost:5000/api/explore/${id}`, 
      { userId },
      { withCredentials: true }
      ) //userId stuur je nu naar de server
      .then(() => {
        console.log(userId);
      })
      .catch((error) => console.error(error));
  }
  return (
    <div>
    <form><button onClick={addToMyHabits}>Add to myHabits</button></form>          
    </div>
  );
}

export default MyHabitButton;
