import React, { useState, useEffect } from "react";
import axios from "axios";
import "bulma/css/bulma.css";
import { Link } from "react-router-dom";

function MyHabits(props) {
  const [myHabitsState, setMyHabits] = useState([]);
  //   console.log(props.loggedInUser)
  //   const user = props.loggedInUser
  //  const userId = props.loggedInUser._id || null //id van user

  function getMyHabits() {
    axios
      .get(
        "http://localhost:5000/api/my-habits",
        // { userId },
        { withCredentials: true }
      )
      .then((userFromDB) => {
        console.log(userFromDB);
        setMyHabits(userFromDB.data.myHabits);
      })
      .catch((error) => console.log(error));
  }

  useEffect(getMyHabits, []);
  return (
    <div>
 <h1 className="title is-1 is-family-code">Explore all habits</h1>
         <div class="columns is-multiline">
            {listState.map((habit) => {
              return (

                  <div class="column is-one-quarter">
                    <div className="card">
                      <div className="card-content" key={habit._id}>
                        <h3 className="subtitle is-6 has-text-weight-semibold">
                          {habit.habitname}{" "}
                        </h3>
                        <p className="content">{habit.description} </p>
                        <p className="content">{habit.categories} </p>
                        <br></br>
                        <footer className="card-footer">
                          <Link
                            className="card-footer-item"
                            to={`/explore/${habit._id}`}
                          >
                            <h3>See more</h3>
                          </Link>
                        </footer>
                      </div>
                    </div>
                  </div>
              );
            })}</div>
    <div>
        <Footer />
      </div>





      <div>
        <h1>My Habits</h1>
      </div>
      <div className="columns">
      {myHabitsState.map((oneMyHabit) => {
        return (
          <div className="column is-half" key={oneMyHabit._id}>
            <h1>{oneMyHabit.habitname}</h1>
            <p>{oneMyHabit.description}</p>
            <p>{oneMyHabit.categories}</p>
            <Link to={`/explore/${oneMyHabit._id}`}>
              <h3>See more</h3>
            </Link>
          </div>
        );
      })}</div>
    </div>
  );
}




export default MyHabits;
